// Display the content of RollData in the console
// it can be used to refer to an actor stat in an inline roll

// get an array with all the selected token
const tokens = canvas.tokens.controlled;

if (tokens.length != 1 ){
  ui.notifications.info("No Tokens or too many tokens were selected");
} else {
  let actor = tokens[0].actor;
  let data = actor.getRollData();
  console.log(data);
}
