const tokens = canvas.tokens.controlled;

if (tokens.length == 1 ){
  await updateIcons(tokens[0]);
} else {
  ui.notifications.info("No Tokens or too many tokens were selected");
}

async function updateIcons(token){
  if (!token.isOwner) {
    ui.notifications.info("You don't own that token");
  }
  let actor = token.actor;


  const items = actor.items.filter(e => e.type == "ability" || e.type == "virtue" || e.type == "flaw");
  ui.notifications.info(`${items.length} items found`);

  let updatedItems = []
  for (let it of items) {
    if (it.img == CONFIG.ARM5E_DEFAULT_ICONS[it.type] ||
    it.img === "icons/svg/mystery-man.svg" ||
    it.img === "icons/svg/item-bag.svg") {

      console.log(it.name);
      let itemData = {_id : it._id, img : CONFIG.Item.systemDataModels[it.type].getIcon(it)};
      updatedItems.push(itemData);
    }
  }
  let updated = await actor.updateEmbeddedDocuments("Item",updatedItems);

  ui.notifications.info(`${updated.length} items updated`);

}
