const tokens = canvas.tokens.controlled;

if (tokens.length == 1 ){
  await quickCombat(tokens[0]);
} else {
  ui.notifications.info("No Tokens or too many tokens were selected");
}

async function quickCombat(token){
  if (!token.isOwner) {
    ui.notifications.info("You don't own that token");
  }
  let actor = token.actor;


  await actor.sheet.quickCombat(token.name);

}


function printMessage(message){
  let chatData = {
    user : game.user._id,
    content : message,
    blind: true,
    whisper : game.users.filter(u => u.isGM).map(u => u.id)
  };
  ChatMessage.create(chatData,{});	
}
