const tokens = canvas.tokens.controlled;

if (tokens.length == 1 ){
  await fixLab(tokens[0]);
} else {
  ui.notifications.info("No Tokens or too many tokens were selected");
}

async function fixLab(token){
  if (!token.isOwner) {
    ui.notifications.info("You don't own that token");
  }
  let actorSheet = token.actor.sheet;
  if (token.actor.type === "laboratory") {
    await actorSheet._resetPlanning()
    ui.notifications.info(`Lab reset`);
  }
  else {
      ui.notifications.info(`Not a lab`);
  }

}
