const tokens = canvas.tokens.controlled;

if (tokens.length == 0 ){
    ui.notifications.info("No Tokens are selected.");
} else {
    await rollForDamage(tokens);

}

async function rollForDamage(tokens){

  await game.arm5e.ArsApps.rollForDamage(tokens)


}


function printMessage(message){
  let chatData = {
    user : game.user._id,
    content : message,
    blind: true,
    whisper : game.users.filter(u => u.isGM).map(u => u.id)
  };
  ChatMessage.create(chatData,{});	
}
