const itemId = "<ITEM_ID>";

// look in the world items


let item = game.items.get(itemId);
if (item) {
    console.log("Item found in the world: " + item.name);
    return;
}

// look into each actor

for (const a of game.actors)
{

    item = a.items.get(itemId);
    if (item) {
        console.log("Item with id " + itemId + " is named \"" + item.name + "\" of type \"" +item.type + "\" is found in actor \"" +a.name +"\".");
        return;        
    }
}

console.log("Item with id \"" +itemId+ "\" not found.");
